import { normalize, resolve } from 'node:path';
export function normalizePath(path) {
    if (path === '') {
        throw new Error('Path cannot be empty');
    }
    const resolvedPath = (normalize(resolve(path)));
    if (process.platform === 'win32') {
        if (resolvedPath.endsWith('\\')) {
            return resolvedPath;
        }
        return `${resolvedPath}\\`;
    }
    if (resolvedPath.endsWith('/')) {
        return resolvedPath;
    }
    return `${resolvedPath}/`;
}
